sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   // when Withdraw request is ticked, set "Withdrawn reason" to show and is mandatory unless we're loading
	// and set Deny request to read-only
	if (newValue === 'true') {
		if (!isLoading)
			g_form.clearValue('u_withdrawn_reason');
		g_form.setDisplay('u_withdrawn_reason', true);
		g_form.setMandatory('u_withdrawn_reason', true);
		g_form.setReadOnly('u_denied', true);
	} else {
		g_form.setMandatory('u_withdrawn_reason', false);
		g_form.setDisplay('u_withdrawn_reason', false);
		g_form.setReadOnly('u_denied', false);
	}	

}
